import logo from './logo.svg';
import React, { useState } from 'react';  
import './App.css';


function App() {

  //Uses the useState hooks, returns text and an update function
  const [text, setText] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  function handleChange(event) {
    setText(event.target.value);
  }

  function searchAndDisplay(){
    let pokemonIndex = getPokemonIndex(text);
    let imageUrl = "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/" + pokemonIndex + ".png";
    setImageUrl(imageUrl)
  } 

  function getPokemonIndex(name) {
    let pokemonIndex;
    console.log(name)
    fetch('https://pokeapi.co/api/v2/pokemon/' + name)
      .then(response => console.log(response.json()))
      .then(response => pokemonIndex = response.json()["held_items"]["id"])
      .catch(error => console.error(error))

    return pokemonIndex;
  }


  return (
    <div className="App">
      <header className="App-header">
        <input value={text} onChange={handleChange} type="text" />
        <button onClick={searchAndDisplay}> search </button>
        <img src = {imageUrl} ></img>
      </header>
    </div>
  );
}

export default App;
